import React, { useState } from 'react';
import { useTranslation } from 'react-i18next';
import { apiEndpoints } from '../services/api';
import {
  MagnifyingGlassIcon,
  CheckCircleIcon,
  XCircleIcon,
  ClockIcon,
  ExclamationTriangleIcon,
  DocumentTextIcon,
} from '@heroicons/react/24/outline';

const Eligibility = () => {
  const { t } = useTranslation();
  const [patientId, setPatientId] = useState('');
  const [serviceType, setServiceType] = useState('general');
  const [eligibilityResult, setEligibilityResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const serviceTypes = [
    { value: 'general', label: 'General Consultation' },
    { value: 'specialist', label: 'Specialist Consultation' },
    { value: 'surgery', label: 'Surgery' },
    { value: 'emergency', label: 'Emergency Care' },
    { value: 'lab_work', label: 'Laboratory Tests' },
    { value: 'imaging', label: 'Medical Imaging' },
  ];

  const handleEligibilityCheck = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setEligibilityResult(null);

    try {
      const response = await apiEndpoints.checkEligibility({
        patient_id: patientId,
        service_type: serviceType,
      });
      setEligibilityResult(response.data);
    } catch (error) {
      if (error.response?.status === 404) {
        setError('Patient not found. Please verify the Patient ID.');
      } else {
        setError('Eligibility check failed. Please try again.');
      }
      console.error('Eligibility check error:', error);
    } finally {
      setLoading(false);
    }
  };

  const getStatusIcon = (eligible) => {
    return eligible ? (
      <CheckCircleIcon className="w-6 h-6 text-green-500" />
    ) : (
      <XCircleIcon className="w-6 h-6 text-red-500" />
    );
  };

  const getStatusColor = (eligible) => {
    return eligible ? 'text-green-700 bg-green-50' : 'text-red-700 bg-red-50';
  };

  return (
    <div className="space-y-6">
      <div className="bg-white shadow rounded-lg">
        <div className="px-4 py-5 sm:p-6">
          <h3 className="text-lg leading-6 font-medium text-gray-900 mb-4">
            Insurance Eligibility Verification
          </h3>

          <form onSubmit={handleEligibilityCheck} className="space-y-4">
            <div className="grid grid-cols-1 gap-4 sm:grid-cols-2">
              <div>
                <label htmlFor="patientId" className="block text-sm font-medium text-gray-700">
                  Patient ID
                </label>
                <input
                  type="text"
                  id="patientId"
                  value={patientId}
                  onChange={(e) => setPatientId(e.target.value)}
                  className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500"
                  placeholder="Enter Patient ID (e.g., P001)"
                  required
                />
              </div>

              <div>
                <label htmlFor="serviceType" className="block text-sm font-medium text-gray-700">
                  Service Type
                </label>
                <select
                  id="serviceType"
                  value={serviceType}
                  onChange={(e) => setServiceType(e.target.value)}
                  className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500"
                >
                  {serviceTypes.map((type) => (
                    <option key={type.value} value={type.value}>
                      {type.label}
                    </option>
                  ))}
                </select>
              </div>
            </div>

            {error && (
              <div className="bg-red-50 border border-red-200 text-red-600 px-4 py-3 rounded-md">
                {error}
              </div>
            )}

            <div>
              <button
                type="submit"
                disabled={loading || !patientId}
                className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-primary-600 hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500 disabled:opacity-50"
              >
                {loading ? (
                  <ClockIcon className="w-4 h-4 mr-2 animate-spin" />
                ) : (
                  <MagnifyingGlassIcon className="w-4 h-4 mr-2" />
                )}
                {loading ? 'Checking...' : 'Check Eligibility'}
              </button>
            </div>
          </form>
        </div>
      </div>

      {eligibilityResult && (
        <div className="space-y-6">
          {/* Eligibility Status */}
          <div className="bg-white shadow rounded-lg">
            <div className="px-4 py-5 sm:p-6">
              <div className="flex items-center justify-between mb-4">
                <h3 className="text-lg leading-6 font-medium text-gray-900">
                  AI-Powered Eligibility Status
                </h3>
                <div className={`inline-flex items-center px-3 py-1 rounded-full text-sm font-medium ${getStatusColor(eligibilityResult.eligible)}`}>
                  {getStatusIcon(eligibilityResult.eligible)}
                  <span className="ml-2">
                    {eligibilityResult.eligible ? 'Eligible' : 'Not Eligible'}
                  </span>
                  <span className="ml-2 text-xs bg-blue-100 text-blue-800 px-2 py-1 rounded">
                    Confidence: {Math.round(eligibilityResult.confidence_score * 100)}%
                  </span>
                </div>
              </div>

              <div className="grid grid-cols-1 gap-4 sm:grid-cols-2">
                <div>
                  <h4 className="text-sm font-medium text-gray-700 mb-2">Patient Information</h4>
                  <div className="space-y-1 text-sm text-gray-600">
                    <p><strong>Name:</strong> {eligibilityResult.patient_info.name}</p>
                    <p><strong>DOB:</strong> {eligibilityResult.patient_info.dob}</p>
                    <p><strong>Insurance ID:</strong> {eligibilityResult.patient_info.insurance_id}</p>
                  </div>
                </div>

                <div>
                  <h4 className="text-sm font-medium text-gray-700 mb-2">Insurance Verification</h4>
                  <div className="space-y-1 text-sm text-gray-600">
                    <p><strong>Provider:</strong> {eligibilityResult.insurance_verification?.provider_info?.name}</p>
                    <p><strong>Country:</strong> {eligibilityResult.insurance_verification?.provider_info?.country}</p>
                    <p><strong>Status:</strong> <span className={eligibilityResult.insurance_verification?.policy_active ? 'text-green-600' : 'text-red-600'}>
                      {eligibilityResult.insurance_verification?.policy_active ? 'Active' : 'Inactive'}
                    </span></p>
                    <p><strong>Verified:</strong> {eligibilityResult.insurance_verification?.verification_method}</p>
                  </div>
                </div>
              </div>
            </div>
          </div>

          {/* AI Coverage Analysis */}
          <div className="bg-white shadow rounded-lg">
            <div className="px-4 py-5 sm:p-6">
              <h3 className="text-lg leading-6 font-medium text-gray-900 mb-4">
                AI Coverage Analysis
              </h3>

              <div className="grid grid-cols-1 gap-4 sm:grid-cols-2 lg:grid-cols-4">
                <div className="bg-gray-50 p-4 rounded-lg">
                  <p className="text-sm font-medium text-gray-700">Deductible</p>
                  <p className="text-lg font-semibold text-gray-900">
                    ${eligibilityResult.coverage_analysis?.deductible || 0}
                  </p>
                </div>

                <div className="bg-gray-50 p-4 rounded-lg">
                  <p className="text-sm font-medium text-gray-700">Copay</p>
                  <p className="text-lg font-semibold text-gray-900">
                    ${eligibilityResult.coverage_analysis?.copay || 0}
                  </p>
                </div>

                <div className="bg-gray-50 p-4 rounded-lg">
                  <p className="text-sm font-medium text-gray-700">Coverage</p>
                  <p className="text-lg font-semibold text-gray-900">
                    {eligibilityResult.coverage_analysis?.coverage_percentage || 0}%
                  </p>
                </div>

                <div className="bg-gray-50 p-4 rounded-lg">
                  <p className="text-sm font-medium text-gray-700">Out-of-Pocket Max</p>
                  <p className="text-lg font-semibold text-gray-900">
                    ${eligibilityResult.coverage_analysis?.out_of_pocket_max || 0}
                  </p>
                </div>
              </div>

              {eligibilityResult.coverage_analysis?.prior_auth_required && (
                <div className="mt-4 bg-yellow-50 border border-yellow-200 rounded-md p-4">
                  <div className="flex">
                    <ExclamationTriangleIcon className="h-5 w-5 text-yellow-400" />
                    <div className="ml-3">
                      <h3 className="text-sm font-medium text-yellow-800">
                        Prior Authorization Required
                      </h3>
                      <p className="mt-1 text-sm text-yellow-700">
                        This service requires prior authorization before proceeding.
                      </p>
                    </div>
                  </div>
                </div>
              )}
            </div>
          </div>

          {/* AI Cost Prediction */}
          <div className="bg-white shadow rounded-lg">
            <div className="px-4 py-5 sm:p-6">
              <h3 className="text-lg leading-6 font-medium text-gray-900 mb-4">
                AI Cost Prediction
              </h3>

              <div className="grid grid-cols-1 gap-4 sm:grid-cols-2 lg:grid-cols-4">
                <div className="bg-blue-50 p-4 rounded-lg">
                  <p className="text-sm font-medium text-blue-700">Total Cost</p>
                  <p className="text-2xl font-bold text-blue-900">
                    ${eligibilityResult.cost_prediction?.estimated_total_cost || 0}
                  </p>
                </div>

                <div className="bg-green-50 p-4 rounded-lg">
                  <p className="text-sm font-medium text-green-700">Patient Responsibility</p>
                  <p className="text-2xl font-bold text-green-900">
                    ${eligibilityResult.cost_prediction?.patient_responsibility || 0}
                  </p>
                </div>

                <div className="bg-purple-50 p-4 rounded-lg">
                  <p className="text-sm font-medium text-purple-700">Insurance Pays</p>
                  <p className="text-2xl font-bold text-purple-900">
                    ${eligibilityResult.cost_prediction?.insurance_payment || 0}
                  </p>
                </div>

                <div className="bg-orange-50 p-4 rounded-lg">
                  <p className="text-sm font-medium text-orange-700">Confidence</p>
                  <p className="text-2xl font-bold text-orange-900">
                    {Math.round((eligibilityResult.cost_prediction?.confidence || 0) * 100)}%
                  </p>
                </div>
              </div>

              {eligibilityResult.cost_prediction?.cost_range && (
                <div className="mt-4 bg-gray-50 p-4 rounded-lg">
                  <p className="text-sm font-medium text-gray-700 mb-2">Cost Range</p>
                  <p className="text-sm text-gray-600">
                    ${eligibilityResult.cost_prediction.cost_range.min} - ${eligibilityResult.cost_prediction.cost_range.max}
                  </p>
                </div>
              )}
            </div>
          </div>

          {/* AI Recommendations */}
          {eligibilityResult.ai_recommendations && (
            <div className="bg-white shadow rounded-lg">
              <div className="px-4 py-5 sm:p-6">
                <h3 className="text-lg leading-6 font-medium text-gray-900 mb-4">
                  AI-Powered Recommendations
                </h3>

                <div className="grid grid-cols-1 gap-6 lg:grid-cols-3">
                  {/* Priority Actions */}
                  <div>
                    <h4 className="text-sm font-medium text-red-700 mb-3">Priority Actions</h4>
                    <div className="space-y-2">
                      {eligibilityResult.ai_recommendations.priority_actions?.map((action, index) => (
                        <div key={index} className="flex items-start">
                          <ExclamationTriangleIcon className="w-4 h-4 text-red-500 mt-0.5 mr-2 flex-shrink-0" />
                          <p className="text-sm text-gray-700">{action}</p>
                        </div>
                      ))}
                    </div>
                  </div>

                  {/* Cost Optimization */}
                  <div>
                    <h4 className="text-sm font-medium text-green-700 mb-3">Cost Optimization</h4>
                    <div className="space-y-2">
                      {eligibilityResult.ai_recommendations.cost_optimization?.map((tip, index) => (
                        <div key={index} className="flex items-start">
                          <CheckCircleIcon className="w-4 h-4 text-green-500 mt-0.5 mr-2 flex-shrink-0" />
                          <p className="text-sm text-gray-700">{tip}</p>
                        </div>
                      ))}
                    </div>
                  </div>

                  {/* Risk Mitigation */}
                  <div>
                    <h4 className="text-sm font-medium text-blue-700 mb-3">Risk Mitigation</h4>
                    <div className="space-y-2">
                      {eligibilityResult.ai_recommendations.risk_mitigation?.map((risk, index) => (
                        <div key={index} className="flex items-start">
                          <DocumentTextIcon className="w-4 h-4 text-blue-500 mt-0.5 mr-2 flex-shrink-0" />
                          <p className="text-sm text-gray-700">{risk}</p>
                        </div>
                      ))}
                    </div>
                  </div>
                </div>

                {/* AI Insights */}
                {eligibilityResult.ai_recommendations.ai_recommendations && (
                  <div className="mt-6 bg-blue-50 border border-blue-200 rounded-lg p-4">
                    <h4 className="text-sm font-medium text-blue-800 mb-2">AI Insights</h4>
                    <div className="space-y-2">
                      {Array.isArray(eligibilityResult.ai_recommendations.ai_recommendations) ? 
                        eligibilityResult.ai_recommendations.ai_recommendations.map((insight, index) => (
                          <p key={index} className="text-sm text-blue-700">
                            {typeof insight === 'object' ? insight.description || insight.title : insight}
                          </p>
                        )) :
                        <p className="text-sm text-blue-700">{eligibilityResult.ai_recommendations.ai_recommendations}</p>
                      }
                    </div>
                  </div>
                )}
              </div>
            </div>
          )}

          {/* Fraud Analysis */}
          {eligibilityResult.fraud_analysis && (
            <div className="bg-white shadow rounded-lg">
              <div className="px-4 py-5 sm:p-6">
                <h3 className="text-lg leading-6 font-medium text-gray-900 mb-4">
                  AI Fraud Analysis
                </h3>

                <div className="flex items-center justify-between mb-4">
                  <div className="flex items-center">
                    <div className={`w-3 h-3 rounded-full mr-2 ${
                      eligibilityResult.fraud_analysis.fraud_risk_level === 'low' ? 'bg-green-500' :
                      eligibilityResult.fraud_analysis.fraud_risk_level === 'medium' ? 'bg-yellow-500' :
                      'bg-red-500'
                    }`}></div>
                    <span className="text-sm font-medium text-gray-700">
                      Risk Level: {eligibilityResult.fraud_analysis.fraud_risk_level?.toUpperCase()}
                    </span>
                  </div>
                  <span className="text-xs bg-gray-100 text-gray-800 px-2 py-1 rounded">
                    Confidence: {Math.round((eligibilityResult.fraud_analysis.confidence || 0) * 100)}%
                  </span>
                </div>

                {eligibilityResult.fraud_analysis.recommended_actions && (
                  <div className="space-y-2">
                    {eligibilityResult.fraud_analysis.recommended_actions.map((action, index) => (
                      <div key={index} className="flex items-start">
                        <CheckCircleIcon className="w-4 h-4 text-green-500 mt-0.5 mr-2 flex-shrink-0" />
                        <p className="text-sm text-gray-700">{action}</p>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            </div>
          )}
        </div>
      )}

      {/* Demo Information */}
      <div className="bg-gradient-to-r from-blue-50 to-purple-50 border border-blue-200 rounded-lg p-4">
        <div className="flex">
          <DocumentTextIcon className="w-5 h-5 text-blue-500 mt-0.5 mr-3 flex-shrink-0" />
          <div>
            <h4 className="text-sm font-medium text-blue-800">AI-Native RCM Demo</h4>
            <p className="text-sm text-blue-700 mt-1">
              Try: <strong>P001</strong> (Ahmed Al-Rashid - Daman Insurance) or <strong>P002</strong> (Fatima Al-Zahra - Tawuniya Insurance)
            </p>
            <p className="text-xs text-blue-600 mt-2">
               Powered by AI: Real-time verification, intelligent cost prediction, fraud detection, and comprehensive recommendations
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Eligibility;
