import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Request interceptor to add auth token
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('authToken');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Response interceptor to handle errors
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      localStorage.removeItem('authToken');
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

// API endpoints
export const apiEndpoints = {
  // Health check
  healthCheck: () => api.get('/'),

  // Auth endpoints
  login: (credentials) => api.post('/auth/login', credentials),
  register: (userData) => api.post('/auth/register', userData),
  logout: () => api.post('/auth/logout'),

  // Eligibility endpoints
  checkEligibility: (patientData) => api.post('/eligibility/check', patientData),
  getEligibilityHistory: (patientId) => api.get(`/eligibility/history/${patientId}`),

  // Prior Authorization endpoints
  submitPriorAuth: (authData) => api.post('/prior-auth/submit', authData),
  getPriorAuthStatus: (authId) => api.get(`/prior-auth/status/${authId}`),
  getPriorAuthList: () => api.get('/prior-auth/list'),

  // Claims endpoints
  submitClaim: (claimData) => api.post('/claims/submit', claimData),
  getClaimsList: (params = {}) => api.get('/claims/list', { params }),
  getClaimStatus: (claimId) => api.get(`/claims/status/${claimId}`),
  getClaimsAnalytics: () => api.get('/claims/analytics'),
  batchSubmitClaims: (claimsData) => api.post('/claims/batch-submit', claimsData),

  // Clinical Documentation endpoints
  getDocTemplates: (category) => api.get('/clinical-docs/templates', { params: { category } }),
  getTemplate: (templateId) => api.get(`/clinical-docs/templates/${templateId}`),
  requestAiAssistance: (data) => api.post('/clinical-docs/ai-assist', data),
  saveDocument: (docData) => api.post('/clinical-docs/save', docData),
  getDocuments: (params = {}) => api.get('/clinical-docs/documents', { params }),
  getDocument: (docId) => api.get(`/clinical-docs/documents/${docId}`),
  updateDocument: (docId, docData) => api.put(`/clinical-docs/documents/${docId}`, docData),
  validateDocument: (docData) => api.post('/clinical-docs/validate', docData),

  // Dashboard endpoints
  getDashboardStats: () => api.get('/dashboard/stats'),
};

export default api;
